window.addEvent("domready",function(){$$(".editable").each(function(a){a.addEvent("dblclick",function(){var c=a.get("html").trim();a.set("html","");if(a.hasClass("textarea")){var b=new Element("textarea",{"class":"box",text:c})}else{var b=new Element("input",{"class":"box",value:c});b.addEvent("keydown",function(d){if(d.key=="enter"){this.fireEvent("blur")}})}b.inject(a).select();b.addEvent("blur",function(){val=b.get("value").trim();a.set("text",val).addClass(val!=""?"":"editable-empty");var d=new Request({url:"http://www.progamemap.com/include/model/editable.php",data:{id:$("id").value,name:$("name").value,url:$("url").value},method:"post",onRequest:function(){}}).send()})})})});